package javaProject3;

public class DemoConcat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			String str = "Welcome";
			String name = "Sai";
			System.out.println(str.concat(name));
			System.out.println(str.contains("e"));
			System.out.println(str.contains("i"));
	}

}
