package javaProject3;

public class LoopClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=0;
		while(i<5) {
			System.out.println("******");
			System.out.println("sunita");
			i++;
	}
		i=0;
		do
		{
			System.out.println("****SUNITA***");
		}while(i<5);
		
		i =0;
		while(i<10) {
			System.out.println(i+1);
		}
}
}