package javaProject3;

public class NestedForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 for( int i=0; i<5; i++)
{
		for( int j=0; j<5; j++) 
{
			System.out.println("i=" +i);
			System.out.println("j=" +j);
}
}

}
}
